import React from "react";
import { ScrollView, ActivityIndicator, StyleSheet, Image, ImageBackground, View, Text, TextInput, TouchableHighlight, TouchableOpacity, Modal, Alert, Animated, Dimensions } from "react-native";
import Filter from './filter'

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchString: '',
            showFilter: false,
            panY: new Animated.Value(Dimensions.get('screen').height)
        };
        this._resetPositionAnim = Animated.timing(this.state.panY, {
            toValue: 0,
            duration: 300,
        })
        this._closeAnim = Animated.timing(this.state.panY, {
            toValue: Dimensions.get('screen').height,
            duration: 500,
        })
    }

    onChangeText(text) {
        this.setState({searchString: text});
        // console.log(this.state.searchString);
    }

    showFilter() {
        this.setState({showFilter: true})
    }

  render() {
    return (
        <View>
            <Modal
                animationType="fade"
                transparent
                visible={this.state.showFilter}
                onRequestClose={() => {
                    Alert.alert("Modal has been closed.");
                }}
            >
                <View style={styles.overlay}>
                    <Animated.View style={[styles.modalView, {top}]}>
                        {this.props.children}
                    </Animated.View>
                </View>
            </Modal>
            <View style={styles.container}>
                <View style={styles.leftMargin}>
                    <TouchableOpacity
                        onPress={() => {
                            this.showFilter();
                        }}>
                        <Image source={require('./assets/images/filter.png')} style={styles.filter}></Image>
                    </TouchableOpacity>
                </View>
                <View style={styles.searchBar}>
                    <TextInput
                        style={styles.searchInput}
                        onChangeText= {text => this.onChangeText(text)}
                    >
                    </TextInput>
                </View>
                <View style={styles.searchButton}>
                    <TouchableOpacity>
                        <Image source={require('./assets/images/magnifying.png')} style={styles.search}></Image>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
  }
}

var styles = StyleSheet.create({

  container: {
    backgroundColor: "white",
    width: '375px',
    height: '60px',
    flexDirection: 'row'
    // top: '50px',
  },
  leftMargin: {
      height: '60px',
      width: '60px',
      justifyContent: 'center',
      alignItems: 'center'
  },
  searchBar: {
      height: '60px',
      width: '255px',
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'center'
  },
  searchButton: {
      height: '60px',
      width: '60px',
      justifyContent: 'center',
      alignItems: 'center'
  },
  filter: {
      height: '24px',
      width: '24px'
  },
  search: {
      height: '24px',
      width: '24px'
  },
  searchInput: {
    height: '38px',
    width: '255px',
    borderWidth: '1px',
    borderColor: '#979797'
  },
  modalView: {
      width: '375px',
      borderWidth: 1,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'white'
    //   flex: 1
  },
  overlay: {
    backgroundColor: 'rgba(0,0,0,0.1523)',
    flex:1,
    justifyContent: 'flex-end',
    alignItems: 'center'
  }

});

export default SearchBar;
