    constructor(props) {
        super(props);
        this.state = {
            searchString: '',
            showFilter: false,
            phraseFilters: [],
        };
        console.log(this.state.phraseFilters)
    }

    onChangeText(text) {
        this.setState({searchString: text});
        // console.log(this.state.searchString);
    }

    showFilter() {
        this.setState({showFilter: true})
    }

    closeFilter()  {
        this.setState({showFilter: false})
    }

