                            <TouchableOpacity
                                onPress={() => {
                                    if (this.state.phraseFilters.includes(5)) {
                                        this.state.phraseFilters.splice(this.state.phraseFilters.indexOf(5), 1)
                                        console.log(this.state.phraseFilters)
                                    } else {
                                        this.state.phraseFilters.push(5)
